from math import ceil def wellcost(x, y, depth): cost = 0 costm = x for i in range (0, depth): cost += costm costm += y return cost
def main(): x = input() x = int(x) y = input() y = int(y) depth = input() depth = ceil(float(depth)) print(wellcost(x, y, depth))
