#include <iostream>

using namespace std;

struct Tariff {
    double curr;
    double last;
    double price;
};

int main()
{
    int n;
    cin >> n;
    
    Tariff v[n];
    
    for (int i = 0; i < n; ++i) {
        cin >> v[i].last >> v[i].curr >> v[i].price;
    }
    
    double sum = 0.0, tmpsum;
    
    for (int i = 0; i < n; ++i) {
        tmpsum = (v[i].curr - v[i].last) * v[i].price;
        cout << tmpsum << ' ';
        sum += tmpsum;
    }
    
    cout << endl << sum << endl;
    
    return 0;
    
}

