#include <iostream>

using namespace std;

struct Taxi {
    int call_price;
    int dist_price;
    int time_price;
};

struct Route {
    int dist;
    int time;
};

int cost_of_a_trip(Route r, Taxi t)
{
    return t.call_price + \
           r.dist * t.dist_price + \
           r.time * t.time_price;
}

int main()
{
    Route r;
    cin >> r.dist >> r.time;
    
    Taxi t;
    cin >> t.call_price >> t.dist_price >> t.time_price;
    
    cout << cost_of_a_trip(r, t) << endl;
    
    return 0;
    
}

#include <iostream>
#include <algorithm>

using namespace std;

struct Taxi {
    int call_price;
    int dist_free;
    int time_free;
    int dist_price;
    int time_price;
};

struct Route {
    int dist;
    int time;
};

int cost_of_a_trip(Route r, Taxi t)
{
    return t.call_price + \
           max(r.dist - t.dist_free, 0) * t.dist_price + \
           max(r.time - t.time_free, 0) * t.time_price;
}

int main()
{
    Route r;
    cin >> r.dist >> r.time;
    
    Taxi t;
    cin >> t.call_price \
        >> t.dist_free >> t.time_free \
        >> t.dist_price >> t.time_price;
        
    cout << cost_of_a_trip(r, t) << endl;
    
    return 0;
    
}

