#include <iostream>
#include <algorithm>

using namespace std;

int main()
{
    int a, b, n, l, c, h;
    cin >> n >> a >> b >> l >> c >> h;
    
    if (l < h) {
        cout << "No " << c * l << endl;
    } else {
        cout << "Yes " << a + b * max(h - n, 0) << endl;
    }
    
    return 0;
}

#include <iostream>
#include <algorithm>

using namespace std;

int main()
{
    int tens, fives, twoes, ones, cnt_10, cnt_5, cnt_2, cnt_1, sum;
    
   
    cin >> tens >> fives >> twoes >> ones >> sum;
    
    cnt_10 = min(sum / 10, tens);
    cnt_5 = min((sum - cnt_10 * 10) / 5, fives);
    cnt_2 = min((sum - cnt_10 * 10 - cnt_5 * 5) / 2, twoes);
    cnt_1 = min(sum - cnt_10 * 10 - cnt_5 * 5 - cnt_2 * 2, ones);
    
    if (cnt_10 * 10 + cnt_5 * 5 + cnt_2 * 2 + cnt_1 * 1 != sum) {
        if (cnt_5 > 0) {
            --cnt_5;
            cnt_2 = min((sum - cnt_10 * 10 - cnt_5 * 5) / 2, twoes);
            cnt_1 = min((sum - cnt_10 * 10 - cnt_5 * 5 - cnt_2 * 2) / 2, ones);
        } else if (cnt_10 > 0) {
            --cnt_10;
            if (fives > 0) {
                cnt_5++;
                cnt_2 = min((sum - cnt_10 * 10 - cnt_5 * 5) / 2, twoes);
                cnt_1 = min((sum - cnt_10 * 10 - cnt_5 * 5 - cnt_2 * 2) / 2, ones);
            }
        }
    }
    
    if (cnt_10 * 10 + cnt_5 * 5 + cnt_2 * 2 + cnt_1 * 1 == sum) {
        cout << "Tens: " << cnt_10 << endl;
        cout << "Fives: " << cnt_5 << endl;
        cout << "Twoes: " << cnt_2 << endl;
        cout << "Ones: " << cnt_1 << endl;
    } else {
        cout << "No change" << endl;
    }
    
    return 0;
}

