var
    sum, rr, n: real;
begin
    writeln('Введите сумму, процент и срок вклада:');
    //Пример: 1000 12.5
    readln(sum, rr, n);
    //Решим по формуле
    writeln('Сложный процент: ', sum * exp(ln(1 + 0.01 * rr)*n));
end.

#include <iostream>
#include <string>
 
using namespace std;
 
int main()
{
    string s;
    cin >> s;
    if (s.size() != 16) 
	{
        cout << "Wrong length!" << endl;
        return 0;
    }
 
    int sum = 0, tmp;
 
    for (int i = 0; i < 16; ++i) 
	{
        if (i % 2 == 0) 
		{
            tmp = (s[i] - '0') * 2;
            if (tmp > 9) 
			{
                tmp -= 9;
            }
        } 
		else 
		{
            tmp = s[i] - '0';
        }
        sum += tmp;
    }
 
    if (sum % 10 == 0) 
	{
        cout << "Valid number!" << endl;
    } else 
	{
        cout << "Non-valid number!" << endl;
    }
 
    return 0;
}

